<h1 class="h3 mb-2 text-gray-800"><?php echo $page_title; ?></h1><ul class="list-group">    <?php if (empty($categories)): ?>        <p>Список тематических разделов пуст</p>    <?php else: ?>        <div class="card shadow mb-4">            <div class="card-header py-3">                <h6 class="m-0 font-weight-bold text-primary">Список тематических разделов</h6>            </div>            <div class="card-body">                <div class="table-responsive">                    <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">                        <tr>                            <th>ID</th>                            <th>Наименование</th>                            <th>Описание</th>                            <th>Редактировать</th>                            <th>Удалить</th>                        </tr>                        <?php foreach ($categories as $category): ?>                            <tr>                                <td>                                    <?php echo $category['category_id']; ?>                                </td>                                <td>                                    <?php echo $category['category']; ?>                                </td>                                <td>                                    <?php echo $category['description']; ?>                                </td>                                <td>                                    <a class="btn btn-warning btn-circle btn-sm"                                       href="/admin/edit/category/<?php echo $category['category_id']; ?>">                                        <i class="fas fa-edit"></i>                                    </a>                                </td>                                <td>                                    <a class="btn btn-danger btn-circle btn-sm" href="#"                                       onclick="delete_commentary_confirm('/admin/delete/category/<?php echo $category['category_id']; ?>');">                                        <i class="fas fa-trash"></i>                                    </a>                                </td>                            </tr>                        <?php endforeach; ?>                    </table>                    <a href="/admin/add/category" class="btn btn-success btn-icon-split justify-content-lg-start">                        <span class="icon text-white-50"><i class="fas fa-plus-circle"></i></span>                        <span class="text">Добавить тематический раздел</span>                    </a>                </div>            </div>        </div>    <?php endif; ?></ul>