<h1 class="h3 mb-2 text-gray-800"><?php echo $page_title; ?></h1><ul class="list-group">    <?php if (empty($exhibitions)): ?>        <p>Список выставок пуст</p>    <?php else: ?>        <div class="card shadow mb-4">            <div class="card-header py-3">                <h6 class="m-0 font-weight-bold text-primary">Список выставок</h6>            </div>            <div class="card-body">                <div class="table-responsive">                    <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">                        <tr>                            <th>ID</th>                            <th>Имя</th>                            <th>Описание</th>                            <th>Место проведения</th>                            <th>Экспонаты</th>                            <th>Даты</th>                            <th>Редактировать</th>                            <th>Удалить</th>                        </tr>                        <?php foreach ($exhibitions as $exhibition): ?>                            <tr>                                <td>                                    <?php echo $exhibition['exhibition_id']; ?>                                </td>                                <td>                                    <?php echo $exhibition['name']; ?>                                </td>                                <td>                                    <?php echo $exhibition['description']; ?>                                </td>                                <td>                                    <?php echo $exhibition['country'] . '; ' . $exhibition['town'] . '; ' . $exhibition['place']; ?>                                </td>                                <td>                                    <?php                                    $stroke = "";                                    foreach ($exhibits[$exhibition['exhibition_id']] as $exhibit)                                    {                                        $stroke .= $exhibit['name'] . '; ';                                    }                                    echo $stroke;                                    ?>                                </td>                                <td>                                    <?php echo $exhibition['start_date'] . ' — ' . $exhibition['end_date']; ?>                                </td>                                <td>                                    <a class="btn btn-warning btn-circle btn-sm"                                       href="/admin/edit/exhibition/<?php echo $exhibition['exhibition_id']; ?>">                                        <i class="fas fa-edit"></i>                                    </a>                                </td>                                <td>                                    <a class="btn btn-danger btn-circle btn-sm" href="#"                                       onclick="delete_commentary_confirm('/admin/delete/exhibition/<?php echo $exhibition['exhibition_id']; ?>');">                                        <i class="fas fa-trash"></i>                                    </a>                                </td>                            </tr>                        <?php endforeach; ?>                    </table>                    <a href="/admin/add/exhibition" class="btn btn-success btn-icon-split justify-content-lg-start">                        <span class="icon text-white-50"><i class="fas fa-plus-circle"></i></span>                        <span class="text">Организовать выставку</span>                    </a>                </div>            </div>        </div>    <?php endif; ?></ul>