<h1 class="h3 mb-2 text-gray-800"><?php echo $page_title; ?></h1><ul class="list-group">    <?php if (empty($exhibits)): ?>        <p>Список экспонатов пуст</p>    <?php else: ?>        <div class="card shadow mb-4">            <div class="card-header py-3">                <h6 class="m-0 font-weight-bold text-primary">Список экспонатов</h6>            </div>            <div class="card-body">                <div class="table-responsive">                    <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">                        <tr>                            <th>ID</th>                            <th>Наименование</th>                            <th>Описание</th>                            <th>Автор</th>                            <th>Редактировать</th>                            <th>Удалить</th>                        </tr>                        <?php foreach ($exhibits as $exhibit): ?>                            <tr>                                <td>                                    <?php echo $exhibit['exhibit_id']; ?>                                </td>                                <td>                                    <?php echo $exhibit['name']; ?>                                </td>                                <td>                                    <?php echo $exhibit['description']; ?>                                </td>                                <td>                                    <?php echo $exhibit['author']; ?>                                </td>                                <td>                                    <a class="btn btn-warning btn-circle btn-sm"                                       href="/admin/edit/exhibit/<?php echo $exhibit['exhibit_id']; ?>">                                        <i class="fas fa-edit"></i>                                    </a>                                </td>                                <td>                                    <a class="btn btn-danger btn-circle btn-sm" href="#"                                       onclick="delete_commentary_confirm('/admin/delete/exhibit/<?php echo $exhibit['exhibit_id']; ?>');">                                        <i class="fas fa-trash"></i>                                    </a>                                </td>                            </tr>                        <?php endforeach; ?>                    </table>                </div>            </div>        </div>    <?php endif; ?></ul>